mapper.select:

SELECT  /* mzlist.selectUserMzListWithMzListName */
EXISTS (SELECT  LIST_ID
          FROM  TB_MZ_LIST
         WHERE  USER_ID = #{userId}
           AND  LIST_NAME = #{mzListName}
)

mapper.delete:

DELETE  /* mzlist.deleteAllRestaurantMzList */
  FROM  TB_MZ_LIST_RESTAURANT_REL
 WHERE  LIST_ID = #{mzListId}


DELETE  /* mzlist.deleteRestaurantMzList */
  FROM  TB_MZ_LIST_RESTAURANT_REL
 WHERE  LIST_ID = #{mzListId}
   AND  RESTAURANT_ID NOT IN


    #{restaurantId}
